import "./Gmap.css"

const AnyReactComponent = ({ text }) => <div className='i-c' > <img src={text} /> </div>;

class SimpleMap extends Component {

  constructor(props) {
    super(props);
    this.state = { 
      counter: 0,
      Shop : ""

    };
  }

  static defaultProps = {
    center: {
      lat: 13.7245601,
      lng: 100.4930267
    },
    zoom: 3
  };

  // componentDidMount() {

