  render() {
    // console.log(this.props.center)

    // console.log( this.state.Shop)

    // this.state.Shop.map((idx) =>{
    //   console.log(idx)
    // } )

    const Data = [
      {
        id:1,
        lat:13.7245601,
        lng:100.4920267
      },
      {
        id:2,
        lat:13.7527239,
        lng:100.4506711
      }
    ]

    const DMat = this.props.dataMap

    console.log("asd",this.props.dataMap)

    return (
      // Important! Always set the container height explicitly
      <div style={{ height: '50vh', width: '50%' }}>
        <GoogleMapReact
          bootstrapURLKeys={{ key: "AIzaSyCKbpzk5AcL0ZJZQiaa9SIwk6PLWOP2rRU"}}
          defaultCenter={this.props.center}
          defaultZoom={this.props.zoom}
        >
          {/* <AnyReactComponent
            lat={13.7245601}
            lng={100.4930267}
            text="./icon/mark.png"
          /> */}

          {Data.map((idx) => { 
            return(
              (
                <AnyReactComponent
                key={idx.id}
                lat={idx.lat}
                lng={idx.lng}
                text="./icon/mark.png"
              /> 
              )
            )
           } )}

        </GoogleMapReact>
      </div>
    );
  }
}

{this.props.data.map((movie, index) => (
  <Col key={index} sm={4} md={4} lg={3}>
      <Card style={{ width: '18rem' }}>
          <Card.Img variant="top" src={"http://image.tmdb.org/t/p/w300//"+movie.poster_path} />
          <Card.Body>
              <Card.Title>{movie.title}</Card.Title>
              <Card.Text>
                  {movie.overview}
              </Card.Text>
          </Card.Body>
      </Card>
  </Col>
)
)}

export default SimpleMap;
